class Solution(object):
    def factorial(n):
        res = 1
        for i in range(1,n+1):
            res *= i
        return res
    def waysToReachStair(self, k):
        if k == 0:
            return 2
        if k == 1:
            return 4
        if k == 2:
            return 4
        if k == 3:
            return 3
        if k == 4:
            return 2
        num_jumps = len(str(bin(k-1)))-2
        d = 2**(num_jumps)-k
        if d > num_jumps+1:
            return 0
        else:
            n = num_jumps+1
            return int(factorial(n)/(factorial(n-d)*factorial(d)))
