class Solution {
public:
    std::map<std::tuple<int,int,bool>, int> memo;

    int solve(int i, int jump, bool down, int k) {
        if (i - k >= 2) {
            return 0;
        }
        if (memo.find({i, jump, down}) != memo.end()) {
            return memo[{i, jump, down}];
        }
        int ans = 0;
        if (i == k) {
            ++ans;
        }
        int left = 0;
        if (i > 0 && !down) {
            left = solve(i - 1, jump, true, k);
        }
        int right = solve(i + (1 << jump), jump + 1, false, k);
        return memo[{i, jump, down}] = ans + left + right;
    }

    int waysToReachStair(int k) {
        return solve(1, 0, false, k);
    }
};
