class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(x, y, z):
            if x < 0:
                return 0

            if x > (k + 1) and z > 0:
                return 0

            ans = 0
            if x == k:
                ans += 1

            if y:
                ans += dp(x-1, False, z)
            ans += dp(x + 2 **z, True, z + 1)
            return ans

        return dp(1, True, 0)
