#define ll long long
class Solution
{
public:
    ll waysToReachStair(ll targetStep)
    {
        ll totalWays = 0;
        for (ll i = 0; i < 32; ++i)
        {
            ll numSteps = i + 1;
            ll remainingSteps = (1 << i) - targetStep;
            if (remainingSteps >= 0)  totalWays += calculateCombination(numSteps, remainingSteps);

        }
        return totalWays;
    }

private:
    ll calculateCombination(ll n, ll k)
    {
        if (k > n)  return 0;
        if (k == 0 || k == n)   return 1;
        k = min(k, n - k);
        return computeBinomialCoefficient(n, k);
    }

    ll computeBinomialCoefficient(ll n, ll k)
    {
        ll combinationResult = 1;
        for (ll i = 0; i < k; ++i)
        {
            combinationResult = combinationResult * 1LL * (n - i);
            combinationResult /= (i + 1);
        }
        return combinationResult;
    }
};
