class Solution {
public:
    int solve(int &k, int &jump, int &i, int cons, unordered_map<int, unordered_map<int, unordered_map<int , int>>>&dp){
        if(cons==1 && i>k) return 0;
        if(i>k+1) return 0;
        if(dp[cons][jump][i]!=0){
            if(dp[cons][jump][i]==-1)  return 0;
            else return dp[cons][jump][i];
        }
        int ans=0;
        if(k==i) ans++;
        i-=1;
        if(cons==0) ans+=solve(k, jump, i, 1, dp);
        i+=1;
        int temp=pow(2,jump);
        jump+=1;
        i+=temp;
        ans+=solve(k, jump, i, 0, dp);
        i-=temp;
        jump-=1;

        if(ans==0) dp[cons][jump][i]=-1;
        else dp[cons][jump][i]=ans;
        return ans;
    }
    int waysToReachStair(int ki) {
        int jump=0, i=1, k=ki;
        //cons, jump, i, ans
        unordered_map<int, unordered_map<int, unordered_map<int , int>>>dp;
        return solve(k, jump, i, 0, dp);
    }
};
