class Solution {
public:
    map<vector<int>,int> m;
    int find(bool pre,int ind,int x,int k){
        if(x==k){
            return 1;
        }
        if(x>k+100) return 0;
        if(m.find({x,ind,pre})!=m.end()) return m[{x,ind,pre}];
        int res = 0;
        if(!pre){
            res+=find(true,ind,x-1,k);
        }
        res+=find(false,ind*2,x+ind,k);

        return m[{x,ind,pre}] = res;
    }
    int waysToReachStair(int k) {
        if(k<3){
            if(k==0) return 2;
             return 4;
        }
        int ans = find(false,1,1,k);
        return ans;
    }
};
