class Solution {
public:

    int nCr(int n, int r) {
    if (r > n || r < 0) return 0;
    if (r == 0 || r == n) return 1;
    r = min(r, n - r); // Use the smaller of r and n-r
    long long res = 1;
    for (int i = 0; i < r; ++i) {
        res = res * (n - i) / (i + 1);
        if (res > INT_MAX) return 0; // Prevent overflow
    }
    return (int)res;
}
    int waysToReachStair(int k) {
        if (k == 0) return 2;  // Special case for k=0
    if (k == 1) return 4;  // Special case for k=1

    int ways = 0;
    for (int i = 0; i <= 31; ++i) {
        int twoPowI = 1 << i;
        if (twoPowI >= k) {
            ways += nCr(i + 1, twoPowI - k);
        }
    }
    return ways;
    }
};
