import math

class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k == 0:
            return 2
        if k == 1:
            return 4
        if k == 2:
            return 4
        if k == 3:
            return 3
        if k == 4:
            return 2
        ups = math.ceil(math.log(k, 2))
        downs = 2**ups-k
        if downs > ups+1:
            return 0
        else:
            return math.comb(ups+1, downs)
