class Solution:
    def waysToReachStair(self, k: int) -> int:

        @lru_cache(None)
        def dp(s, j, r):

            if s - 1 > k or j >= 31:
                return 0

            if s == k:
                result = 1
            else:
                result = 0

            if r == False and s != 0:
                result += dp(s - 1, j, True)

            result += dp(s + (1 << j), j + 1, False)

            return result

        return dp(1, 0, False)
