class Solution {
public:
    int fac(int n,int r)
    {
        if(n<(r<<1))
            return fac(n,n-r);
        long long ans=1;
        for(int up=n,dn=1;dn<=r;dn++,up--)
            ans=(ans*up)/dn;
        return ans;
    }
    int waysToReachStair(int k) {
        int ans=0,stair=1,del=1-k,poss=1;
        while(del<=poss)
        {
            if(del>=0)
                ans+=fac(poss,del);
            del+=stair;
            stair<<=1;
            poss++;
        }
        return ans;
    }
};
