class Solution:
    def waysToReachStair(self, k: int) -> int:


        @cache
        def dfs(i, used, jump):
            if i > k+1:
                return 0

            if i == k:
                res = 1
            else:
                res = 0

            if (not used and i):
                res += dfs(i-1, True, jump)

            return res + dfs(i+(1<<jump), False, jump+1)

        return dfs(1, False, 0)
