class Solution {
    private Map<Integer, int[][]> dp = new HashMap<>();
    private int solve(int k, int i, int jump, boolean can) {
        if (i > k + 5) {
            return 0;
        }
        if (jump > 31) {
            return 0;
        }
        int canIndex = can ? 1 : 0;
        if (dp.containsKey(i) && dp.get(i)[jump][canIndex] != -1) {
            return dp.get(i)[jump][canIndex];
        }
        int ans = (i == k) ? 1 : 0;
        if (can) {
            ans += solve(k, i - 1, jump, false);
        }
        if (i + (1 << jump) <= k + 1) {
            ans += solve(k, i + (1 << jump), jump + 1, true);
        }
        if (!dp.containsKey(i)) {
            dp.put(i, new int[32][2]);
            for (int[] arr : dp.get(i)) {
                Arrays.fill(arr, -1);
            }
        }
        dp.get(i)[jump][canIndex] = ans;
        return ans;
    }
    public int waysToReachStair(int k) {
        return solve(k, 1, 0, true);
    }
}
