class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(p, i, j):


            one = dp(True, i-1, j) if p == False and i > 0 else 0
            two = dp(False, i+2**j, j+1) if i <= k or j <= 1 else 0 

            return one + two + (1 if i == k else 0)

        return dp(False, 1, 0)
