class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def ways(i, lastBack=False, j=0):
            if i > k + 1:
                return 0
            count = 0
            if i == k:
                count += 1
            if not lastBack:
                count += ways(i-1, True, j)
            count += ways(i + 2**j, False, j+1)               
            return count

        return ways(1, False, 0)
