class Solution {
    public int waysToReachStair(int k) {
        Map<Integer, Integer> rc10 = new HashMap<>();
        Map<Integer, Integer> rc11 = new HashMap<>();
        Map<Integer, Integer> rc20 = new HashMap<>();
        Map<Integer, Integer> rc21 = new HashMap<>();
        rc10.put(1, 1);
        rc11.put(0, 1);
        Map<Integer, Integer> pre0, pre1, cur0, cur1, tmp;
        pre0 = rc10;
        pre1 = rc11;
        cur0 = rc20;
        cur1 = rc21;
        int ans = 0;
        int j = 0;
        while (!pre0.isEmpty() || !pre1.isEmpty()) {
            ans += pre0.getOrDefault(k, 0);
            ans += pre1.getOrDefault(k, 0);
            cur0.clear();
            cur1.clear();
            for (Map.Entry<Integer, Integer> e : pre0.entrySet()) {
                int np = e.getKey() + (1 << j);
                if (np < k + 2) {
                    cur0.compute(np, (kk, v) -> {
                        if (v == null) return e.getValue();
                        return v + e.getValue();
                    });
                }
                cur1.compute(np - 1, (kk, v) -> {
                    if (v == null) return e.getValue();
                    return v + e.getValue();
                });
            }
            for (Map.Entry<Integer, Integer> e : pre1.entrySet()) {
                int np = e.getKey() + (1 << j);
                if (np < k + 2) {
                    cur0.compute(np, (kk, v) -> {
                        if (v == null) return e.getValue();
                        return v + e.getValue();
                    });
                }
                np--;
                if (np < k + 2) {
                    cur1.compute(np, (kk, v) -> {
                        if (v == null) return e.getValue();
                        return v + e.getValue();
                    });
                }
            }
            // System.out.println(cur0);
            // System.out.println(cur1);
            // System.out.println(j);
            tmp = pre0;
            pre0 = cur0;
            cur0 = tmp;
            tmp = pre1;
            pre1 = cur1;
            cur1 = tmp;
            ++j;
        }

        return ans;
    }
}
