class Solution {
public:
    unsigned long long binomialCoefficient(int n, int k) {
        // Take advantage of symmetry property
        if (k > n - k) k = n - k;
        unsigned long long result = 1;
        for (int i = 0; i < k; ++i) {
            result *= (n - i);
            result /= (i + 1);
        }
        return result;
    }

    int waysToReachStair(int k) {
        int res = 0;
        for (int i = 0; i <= 30; i++) {
            long long l = (1 << i) - (i + 1);
            long long r = (1 << i);

            if (l <= k && k <= r) {
                int d = r - k;
                res += binomialCoefficient(i + 1, d);
            }
        }

        return res;
    }
};
