typedef int ll;
long long calculateNcr(int n, int r){


    long long sum = 1;
  // Calculate the value of n choose r using the binomial coefficient formula
        for(int i = 1; i <= r; i++){
            sum = sum * (n - r + i) / i;
        }

    return sum;


}

class Solution {
public:
    int waysToReachStair(int k) {


        if(k == 0) return 2;

        if(k == 1) return 4;


        int anss = 0;

        /// Start from 0.
        long long lastValue = 2;
        for(int i = 0; i <= 32; i++) {
            ll currentValue = lastValue - 1;
            ll minusPossible = i  +  1;

            if((currentValue) >= k && (currentValue - minusPossible) <= k) {

                ll minusLagbe = currentValue - k;

                anss+=calculateNcr(minusPossible,minusLagbe);


            }

            lastValue*=2;

        }

       // cout << anss << endl;

         /// Start from 1.

        k--;
        lastValue = 2;

        for(int i = 0; i <= 32; i++) {
            ll currentValue = lastValue - 1;
            ll minusPossible = i  +  1;

            if((currentValue) >= k && (currentValue - minusPossible) <= k) {

                ll minusLagbe = currentValue - k;

                anss+=calculateNcr(minusPossible,minusLagbe);


            }

            lastValue*=2;

        }

        return anss;







    }
};
