from math import comb
class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k == 0:
            return 2
        k -= 1
        res = 0
        for i in range(32):
            cur = (1<<i) - 1
            d = cur - k
            if 0 <= d <= i + 1:
                res += comb(i+1, d)
        return res
