class Solution {
public:
    map<string,int> mp;
    int help(int n,int j,bool m,int k){
        if(n>=(k+2) || n<0){
            return 0;
        }
        string s=to_string(n)+"_"+to_string(j)+"_"+to_string(m);
        if(mp.count(s)){
            return mp[s];
        }
        int ans=(n==k) + help(n+j,j*2,1,k);
        if(m!=0){
            ans+=help(n-1,j,0,k);
        }
        return mp[s]=ans;
    }
    int waysToReachStair(int k) {
        return help(1,1,1,k);
    }
};
