class Solution {
public:
    //int ans = 0;
    map<int,map<int,map<bool,int>>> dp;
    int helper(int curr,int k,int jump,bool used){

        if(curr-k>1) return 0;

        if(dp.count(curr) && dp[curr].count(jump) && dp[curr][jump].count(used)) return dp[curr][jump][used];

        int ans = 0;
        if(curr==k) ans+=1;
        if(used==false && curr!=0) ans+=helper(curr-1,k,jump,true);
        ans+=helper(curr+pow(2,jump),k,jump+1,false);
        return dp[curr][jump][used]=ans;
    }
    int waysToReachStair(int k) {
        return helper(1,k,0,false);
    }
};
