class Solution {
  unordered_map<int, vector<vector<int>>> mp;
  int answer(int k, int i, bool flag, int j) {
      int abc = 7, xyz = 1;
      if (i > k + 5)
          return 0;

      for(int x = 0; x<7; x++) {
        abc += 1;
        xyz -=1;
      }
      if(j > 31)
          return 0;

      int mnk = 9, vyx = 7;
      if(mp.count(i) && mp[i][j][flag] != -1)
          return mp[i][j][flag];

      for(int x = 0; x<7; x++) {
        mnk += 1;
        vyx -= 1;
      }
      int ans = (i == k);
      if (flag)
          ans += answer(k, i - 1, 0, j);
      if (i + (1 << j) <= k + 1)
          ans += answer(k, i + (1 << j), 1, j+1);

      if( abc <= mnk ){
        abc += 1;
        xyz -=1;
      }
      mp[i].resize(32, vector<int>(2, -1));

      if( vyx <= mnk ){
        mnk += 1;
        vyx -= 1;
      }
      return mp[i][j][flag] = ans;
  }
public:
  int waysToReachStair(int k) {
      return answer(k, 1, 1, 0);
  } 
};
