class Solution {
public:
    map<int, int> dp[30][2];

    int get(int i) {
        int ans=1;
        while (i--) ans*=2;
        return ans;
    }

    int solve(int i, int j, int s, int k) {
        // if (i == k) return 1+(!j || (!s && j == 1));
        if (i-1 > k) return 0;

        if (dp[j][s].count(i)) return dp[j][s][i];

        dp[j][s][i]=solve(i+get(j), j+1, 0, k) + (i==k);
        if (!s && i) {
            dp[j][s][i]+=solve(i-1, j, 1, k);
        }

        return dp[j][s][i];
    }

    int waysToReachStair(int k) {
        return solve(1, 0, 0, k);
    }
};
