class Solution:
    def __init__(self):
        self.dp = {}

    def solve(self, k, i, j, can):
        if i > k + 5:
            return 0

        if j > 31:
            return 0

        if i in self.dp and self.dp[i][j][can] != -1:
            return self.dp[i][j][can]

        ans = 1 if i == k else 0
        if can:
            ans += self.solve(k, i - 1, j, 0)
        if i + (1 << j) <= k + 1:
            ans += self.solve(k, i + (1 << j), j + 1, 1)

        if i not in self.dp:
            self.dp[i] = [[-1] * 2 for _ in range(32)]

        self.dp[i][j][can] = ans

        return ans

    def waysToReachStair(self, k):
        return self.solve(k, 1, 0, 1)
