class Solution {
public:


    long long C(int n, int r) {
        double sum = 1;
        // Calculate the value of n choose r using the binomial coefficient formula
        for(int i = 1; i <= r; i++){
            sum = sum * (n - r + i) / i;
        }
        return (int)sum;
    }

    int waysToReachStair(int k) {
        int jump = 0;
        long long pos = 1;
        int ans = 0;
        while (pos < k) {
            pos <<= 1;
            jump++;
        }
        while (pos >= k && pos - k <= jump + 1) {
            //printf("%d, %d\n", jump + 1, pos - k);
            ans += C(jump + 1, pos - k);

            pos <<= 1;
            jump++;
        }

        return ans;
    }
};
