class Solution:
    def waysToReachStair(self, k: int) -> int:

        @lru_cache
        def solve(curr, jump, cons):
            canGoDown = curr > 0 and not cons
            if curr > k + 1:
                return 0
            if curr == k+1:
                return solve(curr-1, jump, not cons) if canGoDown else 0
            ans = 0
            if curr == k:
                ans = 1        
            if canGoDown:
                return ans + solve(curr-1, jump, not cons) + solve(curr + 2**jump, jump + 1, False)
            return ans + solve(curr + 2**jump, jump + 1, False)

        return solve(1, 0, False)
