#define MOD 1000000007
#define pb push_back
#define fi first
#define se second
typedef long long lld;
typedef long ld;
#define ordered_set tree<lld, null_type,less<lld>, rb_tree_tag,tree_order_statistics_node_update> 
#define inf LLONG_MAX
const lld MAXN = (int)3e5 + 5;

class Solution {
   lld power(lld x, lld y) 
{ 
    lld res = 1; 
    while (y > 0) { 

        if (y & 1) 
            res = (res * x) % MOD; 
        y = y >> 1; 
        x = (x * x) % MOD; 
    } 
    return res; 
} 
    lld fac(lld x){
        lld ans=1;
        for(lld i=1;i<=x;i++){ans*=i;ans%=MOD;}return ans;

    }
    lld modinv(lld x){
        return power(fac(x),MOD-2);
    }
public:
    int waysToReachStair(int k) {

        long long ans=1;
        int way=0;
        if(ans-1==k)
            way++;
        if(ans==k)
            way++;
        for(int i=1;i<31;i++){
            ans+=1<<(i-1);
            for(int j=0;j<=i+1;j++){
                if(ans-j==k)
                {

                    way+=((fac(i+1)*modinv(j))%MOD)*modinv(i+1-j)%MOD;

                }
            }
        }
        return way;
    }
};
