class Solution:
    def waysToReachStair(self, k: int) -> int:
        @functools.lru_cache(maxsize=None)
        def f(i, jump, last_step_was_down=False):
            if i > k + 1:
                return 0
            elif i == k + 1:
                if last_step_was_down:
                    return 0
                return f(k, jump, True)
            ret = int(i == k)
            ret += f(i + (2 ** jump), jump + 1, False)
            if not last_step_was_down and i > 0:
                ret += f(i - 1, jump, True)
            return ret

        return f(1, 0)
