class Solution:
    def waysToReachStair(self, k: int) -> int:
        @lru_cache(None)
        def helper(jump, curr, ava):
            if curr > k + 1:
                return 0
            res = 1 if curr == k else 0 
            if ava and curr > 0:
                res += helper(jump, curr-1, False)
            res += helper(jump*2, curr+jump, True)
            return res
        return helper(1, 1, True)
