class Solution {
public:
    int nCr(int n, int r) {
        if (r > n) return 0;
        if (r == 0 || n == r) return 1;
        double res = 0;
        for (int i = 0; i < r; i++) {
            res += log(n-i) - log(i+1);
        }
        return (int)round(exp(res));
    }

    int waysToReachStair(int k) {
        if(k == 0) return 2;
        if(k == 1) return 4;

        int sum = 1, exp = 1;

        while(sum < k) { sum <<= 1; exp++; }

        int needs = sum-k;

        return nCr(exp, needs) + nCr(exp+1, needs+sum);

    }
};
