class Solution {
public:
    // unorderd_map<ll,unorderd_map<ll,unorderd_map<<ll,ll>>>dp;
    map<vl,ll>dp;
    ll solve(ll i,ll jump,ll last,ll k){

        // if(i>k+1000) return 0;
        if(dp.count({i,jump,last})) return dp[{i,jump,last}];

        ll count= (i==k) ;
        if(jump<34) count+=solve(i+(1ll<<jump),jump+1,1,k);
        if(last==1){
            count+=solve(i-1,jump,0,k);
        }

        return dp[{i,jump,last}]=count;
    }
    int waysToReachStair(int k) {
        dp.clear();
        return solve(1,0,1,k);
    }
};
