class Solution:
    def waysToReachStair(self, k: int) -> int:
        dp = {}

        def rec(curr, jump, last):
            if (curr > k + 1) or (last and curr >= k + 1):
                return 0

            key = str(curr) + "*" + str(jump) + "*" + str(last)
            if key in dp:
                return dp[key]

            ans = 0
            if curr == k:
                ans = 1

            op1 = 0
            if not last:
                op1 = rec(curr - 1, jump, True)

            op2 = rec(curr + 2 ** jump, jump + 1, False)

            dp[key] = ans + op1 + op2
            return dp[key]

        return rec(1, 0, 0)
