class Solution {
public:
    int recur(int back,int jump,int p,vector<vector<vector<int>>>& dp,int k)
    {
        if((1ll<<jump)-back==k)
        {
            if(jump==0)
            {
                if(p==0)
                return 4;
                return 2;
            }
            if(jump==1)
            {
                if(p==0)
                    return 2;
            }
            return 1;
        }
        if((1ll<<jump)-back>k+1)
            return 0;
        if(dp[back][jump][p]!=-1)
            return dp[back][jump][p];
        int ans=recur(back,jump+1,0,dp,k);
        if(p==0)
            ans+=recur(back+1,jump,1,dp,k);
        return dp[back][jump][p]=ans;
    }
    int waysToReachStair(int k) {
        int cnt=0,k2=k;
        while(k2)
        {
            k2/=2;
            cnt++;
        }
        vector<vector<vector<int>>> dp(cnt+5,vector<vector<int>>(cnt+5,vector<int>(2,-1)));
        return recur(0,0,0,dp,k);
    }
};
