class Solution {
public:
    Solution() {
        cin.tie(NULL);
        cout.tie(NULL);
        ios_base :: sync_with_stdio(false);
    } 

    long long comb(int n, int k) {
        if (k>n || k<0) return 0;
        if (k==0 || k==n) return 1;
        long long res=1;
        for (int i=1;i<=k;i++) {
            res=res*(n-i+1)/i;
        }
        return res;
    }

    int waysToReachStair(int k) {
        long long total = 0;
        for (int i=0;i<32;i++) {
            int n=i+1;
            long long pow_i=1LL<<i;
            if (pow_i>=k) {
                int m=pow_i-k;
                total+=comb(n,m);
            }
        }
        return total;

    }
};
