class Solution:
    def solve(self,i,jump,k,flag,dp):
        if i-1>k:
            return 0

        if i==k and jump>2:
            return 1

        t=(i,jump,flag)
        if t in dp:
            print("yrs")
            return dp[t]
        if i==k:
            x=self.solve(i+2**jump,jump+1,k,0,dp)+1
            y=0
            if flag==0 and i!=0:
                y=self.solve(i-1,jump,k,1,dp)
            dp[t]=x+y
            return x+y

        x=self.solve(i+2**jump,jump+1,k,0,dp)
        y=0
        if flag==0 and i!=0:
            y=self.solve(i-1,jump,k,1,dp)
        dp[t]=x+y
        return x+y



    def waysToReachStair(self, k: int) -> int:
        dp={}
        return self.solve(1,0,k,0,dp)
