class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def f(i, jump, down):
            res = 0
            if i == k:
                res += 1
            if i > k + 1:
                return 0
            if i + 2 ** jump <= k + 1:
                res += f(i + 2 ** jump, jump + 1, 0)
            if i and not down:
                res += f(i - 1, jump, 1)
            return res
        return f(1, 0, 0)
