class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dfs(step,lastOp,jump):
            if step>k+1 or (step==k+1 and lastOp): return 0
            res = (step==k)+dfs(step+(1<<jump),False, jump+1)
            if step!=0 and not lastOp:
                res+=dfs(step-1,True,jump)
            return res
        return dfs(1,False,0)
