class Solution {
public:
    map<tuple<bool,int,int>,int>mp;
    int f(int k,bool used,int ind,long long jump){
        if((ind>k && (!used)) ||(ind<0)) return 0;

        if(mp.count({used,ind,jump})) return mp[{used,ind,jump}];
        long long x=(1LL<<jump);
        int way1=0;
        if(x+ind<=k+1ll){
        way1=f(k,true,ind+x,jump+1ll);
        }
        int way2=0;
        if(used){
            way2=f(k,false,ind-1ll,jump);
        }
        int res=way1+way2;
        if(ind==k) res++;
        return mp[{used,ind,jump}]=(res);
    }
    int waysToReachStair(int k) {
        int ans=f(k,true,1,0ll);

        return ans;
    }
};
