class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def move(jump, stair, prev=False):
            res = 0
            if stair == k:
                res = 1
            jj = stair + pow(2, jump)
            if jj <= k + 1:
                res += move(jump+1, jj)
            if stair > 0 and not prev:
                res += move(jump, stair-1, True)

            return res

        return move(0, 1)
