class Solution {
public:
    int waysToReachStair(int k) {
        typedef long long ll;
        ll jump = 0, s = 1, ans = 0;
        while(true){
            if(s >= k & s-(jump+1) <= k){
                int b = s - k;
                ll cnt = 1;
                if(b*2 < jump+1){
                    b = jump+1-b;
                }
                int j = 2;
                for(int i=jump+1; i>b; i--){
                    cnt *= i;
                    if(j <= jump+1-b & cnt % j == 0){
                        cnt /= j;
                        j++;
                    }
                }
                // for(int i=jump+1-b; i>0; i--){
                //     cnt /= i;
                // }
                ans += cnt;
            }
            else if(s-(jump+1) > k){
                break;
            }
            s += (1<<jump);
            jump++;
        }
        return (int)ans;
    }
};
