class Solution:
  def waysToReachStair(self, k: int) -> int:
    @cache
    def dfs(current: int, prev_operation: int, jump: int) -> int:
      if current > k + 1:
        return 0
      if current == k:
        if prev_operation == 1:
          return 1 if jump > 0 else 2
        if jump == 0:
          return 4
        if jump == 1:
          return 2
        return 1

      ret = 0
      if prev_operation != 1:
        ret += dfs(current - 1, 1, jump)
      ret += dfs(current + (1 << jump), 2, jump + 1)
      return ret
    return dfs(1, -1, 0)
