class Solution {
public:
    int waysToReachStair(int k) {
        int nWays = 0;
        for (int stair = 1, jump = 0; stair - jump - 1 <= k; stair <<= 1, jump++) {
            if (stair - k >= 0) {
                nWays += nChoosek(jump + 1, stair - k);
            }
        }
        return nWays;
    }

    int nChoosek(int n, int k) {
        if (k > n) return 0;
        if (k * 2 > n) k = n-k;
        if (k == 0) return 1;

        long long result = n;
        for(int i = 2; i <= k; ++i) {
            result *= (n-i+1);
            result /= i;
        }
        return result;
    }
};
