class Solution:
    def waysToReachStair(self, k: int) -> int:
        jumps = [0] + [2 ** i for i in range(31)]
        prefix = list(accumulate(jumps))
        print(jumps)
        print(prefix)
        answer = 0
        for i in range(len(jumps)):
            for j in range(i + 2):
                if 1 + prefix[i] - j == k:
                    answer += factorial(i + 1) // factorial(j) // math.factorial(i + 1 - j)
        return answer
