class Solution {
public:
    int solve(int ind, int& k, map<pair<int, pair<int, int>>, int>& dp, int jump, int bk = 0){
        if(ind >= k+2) return 0;

        int ans = 0;
        if(ind == k) ans = 1;
        else if(dp.find({ind, {jump, bk}}) != dp.end()) return dp[{ind, {jump, bk}}];

        ans += solve(ind + pow(2, jump), k, dp, jump + 1, 0);
        if(bk == 0)
        ans += solve(ind-1, k, dp, jump, 1);

        return dp[{ind, {jump, bk}}] = ans;

    }
    int waysToReachStair(int k) {
        map<pair<int, pair<int, int>>, int> dp;

        return solve(1, k, dp, 0);
    }
};
