class Solution {
public:
    int waysToReachStair(int k) {
        long long ans=0;
        vector<vector<long long>>pascal(33,vector<long long>(33,0));
        for(long long i=0;i<=32;i++){
            pascal[i][0]=1;
        }
   for(long long i=1;i<=32;i++){
      for(long long j=1;j<=i;j++){
         pascal[i][j]=pascal[i-1][j]+pascal[i-1][j-1];
      }
   }

   ///mayor x tal que (1LL<<x)-1-x<=k

   ///itero por las s(saltos) desde 0 a x, que son las cantidad de saltos hacia atras
   for(long long i=0;i<=32;i++){
      long long val=k+i;
      ///verifical si es potencia de 2
      long long cont=0;
      if(val==0)continue;
      while(val%2==0){
         val/=2;
         cont++;
      }
      if(val!=1)continue;
      if(cont+1<i)continue;
      ans+=pascal[cont+1][i];
   }
   return ans;
    }
};
