class Solution:
    def waysToReachStair(self, k: int) -> int:
        def f(i,j,n,fg):
            if(i==n):
                if(fg==0):
                    if(i+2**j<=n+1):
                        return 1+f(i-1,j,n,1)+f(i+2**j,j+1,n,0)
                    else:
                        return 1+f(i-1,j,n,1)
                else:
                    if(i+2**j<=n+1):
                        return 1+f(i+2**j,j+1,n,0)
                    else:
                        return 1
            elif(i==n+1):
                if(fg==0):
                    return f(i-1,j,n,1)
                else:
                    return 0
            elif(i>n+1):
                return 0
            elif(mp[fg][j][i]!=0):
                return dp[fg][j][i]
            else:
                if(fg==0):
                    if(i+2**j<=n+1):
                        dp[fg][j][i]=f(i-1,j,n,1)+f(i+2**j,j+1,n,0)
                    else:
                        dp[fg][j][i]=f(i-1,j,n,1)
                else:
                    if(i+2**j<=n+1):
                        dp[fg][j][i]=f(i+2**j,j+1,n,0)
                    else:
                        dp[fg][j][i]= 0
                mp[fg][j][i]=1
                return dp[fg][j][i]
        dp=[[defaultdict(int) for j in range(31)] for i in range(2)]
        mp=[[defaultdict(int) for j in range(31)] for i in range(2)]
        return f(1,0,k,0)
