class Solution {
public:
    int k;
    map<tuple<int, int, int>, int> ma;
    int fun(int num, int prev, int j) {
        if (num >= (k + 2)) return 0;
        if (ma.count(make_tuple(num, prev, j))) return ma[make_tuple(num, prev, j)];
        int ans = 0;
        if (num == k) ans++;

        ans = (ans + fun(num + (1 << j), 0, j + 1));
        if (prev == 0) {
            ans = (ans + fun(num - 1, 1, j));
        }

        return ma[make_tuple(num, prev, j)] = ans;
    }
    int waysToReachStair(int K) {
        k = K;
        return fun(1, 0, 0);
    }
};
