class Solution {
public:

    int ans;
    long long f(vector<vector<vector<long long>>>&dp, int jump, int time, int flag, long long num=1){
        // cout<<num<<endl;
        if (jump >= 30 || time >= 60 || num>=2*ans+2) return 0;
        if (dp[jump][time][flag]!=-1) return dp[jump][time][flag];
        long long temp=0;
        if (num == ans) temp=1;
        if (flag || num==0) return dp[jump][time][flag] = temp+f(dp,jump+1,time+1,0,num+(1<<jump));
        return dp[jump][time][flag] = temp + f(dp,jump,time+1,1,num-1) + f(dp,jump+1,time+1,0,num+(1<<jump));

    }
    int waysToReachStair(int k) {
        vector<vector<vector<long long>>>dp(30,vector<vector<long long>>(60,vector<long long>(2,-1)));
        ans=k;
        return f(dp,0,0,0);
    }
};
