#define ll long long
class Solution {
public:
    map <ll, map<pair<ll, ll>, ll>> mp;

    ll rec(ll i, ll jmp, ll p, ll k){
        if(i > k+3 || jmp >= 31) return 0; 
        if(mp[i].count({jmp, p})) return mp[i][{jmp, p}];
        ll ans = (i == k);
        if(p) ans += rec(i-1, jmp, 0, k) + rec(i + (1ll << jmp), jmp+1, 1, k);
        else ans += rec(i + (1ll << jmp), jmp+1, 1, k);
        return mp[i][{jmp, p}] = ans;
    }

    int waysToReachStair(int k) {
        mp.clear();
        return rec(1ll, 0ll, 1ll, 1ll*k);
    }
};
