class Solution {
public:
    unordered_map<int,unordered_map<int,unordered_map<int,int>>> dp;
    int check(int index,int jump,int flag,int k){
        if(jump>=31) return 0;
        if(dp.find(index)!=dp.end() && dp[index].find(flag)!=dp[index].end() && dp[index][flag].find(jump)!=dp[index][flag].end()) return dp[index][flag][jump];

        if(index>k){
           if(index-k>1) return 0;
           else{
               if(flag!=0){
                   return dp[index][flag][jump]=check(index-1,jump,0,k);
               }
               else{
                   return dp[index][flag][jump]=0;
               }
           } 
        }
        if(index==k){
            // cout<<index<<" "<<flag<<endl;
           int a=0;
            if(flag==0){
            a=1+check(index+pow(2,jump),jump+1,1,k);
            }
           else{
            a=1+check(index+pow(2,jump),jump+1,1,k)+check(index-1,jump,0,k);
            }
            return dp[index][flag][jump]=a;
        }

        int a=0;
        if(flag==0){
            a=check(index+pow(2,jump),jump+1,1,k);
        }
        else{
            // cout<<"hello"<<endl;
            a=check(index+pow(2,jump),jump+1,1,k)+check(index-1,jump,0,k);
        }

        return dp[index][flag][jump]=a;
    } 
    int waysToReachStair(int k) {
       return check(1,0,-1,k); 
    }
};
