class Solution {

    long nCr(long  n, long r)
{
    // return fact(n) / (fact(r) * fact(n - r));
    double sum = 1;
  // Calculate the value of n choose r using the binomial coefficient formula
        for(int i = 1; i <= r; i++){
            sum = sum * (n - r + i) / i;
        }
        return (long)sum;
}

// Returns factorial of n
long fact(long n)
{
      if(n==0)
      return 1;
    long res = 1;
    for (long i = 2; i <= n; i++)
        res = res * i*1ll;
    return res;
}
public:
    int waysToReachStair(int k) {
        if(k==0) return 2;
        if(k==1) return 4;
        k--;

        long long p=k,i=0,j=2*1ll,ans=0;


        while(i<30*1ll){
            j--;
            if(k<=(j)&&k>=(j-(i+2*1ll))){
                ans+=nCr(i+2*1ll,j-k);
                // cout<<ans<<" ";
            }
            j++;
            j*=2*1ll;
            i++;

        }
        int pp=ans;
    return pp;


    }

};
